double GaussLegendre::integrate_0_infty(std::function<double(double)> X, double rb) const
{
    double sum1 = 0.0;
    for (int i=0; i<_num; i++)
    {
        double theta = _xv[i]*0.5*M_PI;
        double s = sin(theta);
        double c = cos(theta);
        double u = rb*s;
        double t = X(u) * (rb*c);
        sum1 += _wv[i]*t;
    }
    sum1 *= 0.5*M_PI;
    double sum2 = 0.0;
    for (int i=0; i<_num; i++)
    {
        double theta = _xv[i]*0.5*M_PI;
        double s = sin(theta);
        double c = cos(theta);
        double u = rb/s;
        double t = X(u) * (rb*c/(s*s));
        sum2 += _wv[i]*t;
    }
    sum2 *= 0.5*M_PI;
    return sum1+sum2;
}

