double GaussLegendre::integrate_0_r(std::function<double(double)> X, double r, double rb) const
{
    if (r<=rb)
    {
        double sum = 0.0;
        for (int i=0; i<_num; i++)
        {
            double theta = _xv[i]*0.5*M_PI;
            double s = sin(theta);
            double c = cos(theta);
            double u = r*s;
            double t = X(u) * (r*c);
            sum += _wv[i]*t;
        }
        sum *= 0.5*M_PI;
        return sum;
    }
    else
    {
        double sum1 = 0.0;
        for (int i=0; i<_num; i++)
        {
            double theta = _xv[i]*0.5*M_PI;
            double s = sin(theta);
            double c = cos(theta);
            double u = rb*s;
            double t = X(u) * (rb*c);
            sum1 += _wv[i]*t;
        }
        sum1 *= 0.5*M_PI;
        double sum2 = 0.0;
        double asinrbr = asin(rb/r);
        for (int i=0; i<_num; i++)
        {
            double theta = asinrbr + _xv[i]*(0.5*M_PI-asinrbr);
            double s = sin(theta);
            double c = cos(theta);
            double u = r*s;
            double t = X(u) * (r*c);
            sum2 += _wv[i]*t;
        }
        sum2 *= (0.5*M_PI-asinrbr);
        return sum1+sum2;
    }
}

