double GaussLegendre::integrate_r_infty(std::function<double(double)> X, double r, double rb) const
{
    double eps = 1e-4;
    if (r/rb >= 1.0-eps)
    {
        double sum = 0.0;
        for (int i=0; i<_num; i++)
        {
            double theta = _xv[i]*0.5*M_PI;
            double s = sin(theta);
            double c = cos(theta);
            double u = r/s;
            double t = X(u) * (r*c/(s*s));
            sum += _wv[i]*t;
        }
        sum *= 0.5*M_PI;
        return sum;
    }
    else
    {
        double sum1 = 0.0;
        double asinrrb = asin(r/rb);
        for (int i=0; i<_num; i++)
        {
            double theta = asinrrb + _xv[i]*(0.5*M_PI-asinrrb);
            double s = sin(theta);
            double c = cos(theta);
            double u = r/s;
            double t = X(u) * (r*c/(s*s));
            sum1 += _wv[i]*t;
        }
        sum1 *= (0.5*M_PI-asinrrb);
        double sum2 = 0.0;
        for (int i=0; i<_num; i++)
        {
            double theta = _xv[i]*0.5*M_PI;
            double s = sin(theta);
            double c = cos(theta);
            double u = rb/s;
            double t = X(u) * (rb*c/(s*s));
            sum2 += _wv[i]*t;
        }
        sum2 *= 0.5*M_PI;
        return sum1+sum2;
    }
}

